import { useState } from 'react'
import { FaTimes } from 'react-icons/fa'

const Task = ({ task, onDelete, onToggle }) => {

  return (
    <div 
      className='task' 
      onDoubleClick={() => onToggle(task.id)}>
      <h3>
        {task.text}{' '}
        <FaTimes style={{ 
          color: 'red', 
          cursor: 'pointer'
        }}
        onClick={() => onDelete(task.id)}/>
      </h3>
      <p>{task.day}</p>
      {task.showDesc && <p>{task.description}</p>}
    </div>
  )
}

export default Task
